import './ExpenseForm.css'
import React, {useState} from 'react';
function ExpenseForm(props) {
    const [enteredTitle, setTitle] = useState("");
    const [enteredAmount,setAmount] = useState("");
    const [enteredDate,setDate] = useState("");

    const titleChangeHandler = (event) => {
        setTitle(event.target.value) // the value passed through input is saved to title variable
    }
    const amountChangeHandler = (event) => {
        setAmount(event.target.value)
    }
    const DateChangeHandler = (event) => {
        setDate(event.target.value)
    }
    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }
        console.log(expenseData)
        setTitle("")
        setAmount("")
        setDate("")
    }

    return (
    <form onSubmit={submitHandler}>
        <div className = "new-expense__controls">
            <div className = "new-expense__control"> 
                <label>Title</label>
                <input type="text" value={enteredTitle} onChange={titleChangeHandler}></input>
            </div>
            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" value={enteredAmount} onChange={amountChangeHandler} min="0.01" step="0.01"></input>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input onChange={DateChangeHandler} value={enteredDate} type="date" min="2019-01-01" max="2022-12-31"></input>
            </div>
        </div>
            <div className="new-expense__actions">
                <button type='submit'>Add Expense</button>
            </div>
    </form>
)
}

export default ExpenseForm;
