import '../../UI/ExpenseItem.css';
import Card from './Card';

import ExpenseTitle from './ExpenseTitle';
import ExpenseDate from './ExpenseDate';
import ItemPrice from './ItemPrice';

function ExpenseItem(props) {
    let title = document.getElementsByClassName("ExpenseTitle"); 
    let titleInput = document.getElementById("title__input");
    console.log(titleInput);
    const changeTitle =()=> {
        title.innerText = titleInput.value;
        console.log(titleInput)
        titleInput.value = ""
    }
    return(
        <Card className="expense-item">
                <ExpenseDate date = {props.date} />
                <ExpenseTitle title = {props.title} />
                <ItemPrice price = {props.price}/>  
                <form> 
                    <input type="text" id='title__input' placeholder='Enter New Name'/>
                </form> 
                <button onClick = {changeTitle}>Change Title</button>     
        </Card> //can only have one root element!
    )
}

export default ExpenseItem;
