import ExpenseItem from './ExpenseItem'
import '../../UI/Items.css'
import Card from './Card'
import ExpensesFilter from './ExpensesFilter';
import React,{useState} from 'react';

function Items(props) {
  // 
  // 
  // const [filteredYear,setFilteredYear] = useState('2022');
  // 
  //   setFilteredYear(selectedYear)
  // }
  // // console.log("line17",filteredYear);
  // const filteredItems = props.expenses.filter( item => 
  //   item.date.getFullYear().toString() === filteredYear)
  let expenses= props.expenses;
  const [filteredExpenses,setFilteredExpenses] = useState(expenses);
  const filterChangeHandler = (selectedYear) => 
      setFilteredExpenses(expenses.filter(expense => 
        expense.date.getFullYear().toString() === selectedYear.toString()
      ))

    return (
      <div>
        <Card className='expenses'>
        <ExpensesFilter  onChangeFilter={filterChangeHandler}/>
     {filteredExpenses.map( expense =>
         <ExpenseItem 
            key={expense.id}
            title={expense.title} 
            price={expense.amount} 
            date={expense.date} />
       )
      }
      </Card>
    </div>
    )
}
export default Items;
