import ExpenseItem from './ExpenseItem'
import '../../UI/Items.css'
import Card from './Card'
import ExpensesFilter from './ExpensesFilter';
import React,{useState} from 'react';

function Items(props) {
  // let expenses = props.expenses;
  // const [filteredExpenses,setFilteredExpenses] = useState(expenses);
  const [filteredYear,setFilteredYear] = useState('2020');
  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear)
    // setFilteredExpenses(expenses => expenses.filter(expense => 
    //   expense.date.getFullYear().toString() === selectedYear.toString()
    // ))
  }
  const filteredExpeses = props.items.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear ;
  })
  // console.log("line20",filteredExpenses);
    return (
      <div>
        <Card className='expenses'>
        <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler}/>
     {filteredExpeses.map( expense =>
         <ExpenseItem 
            key={expense.id}
            title={expense.title} 
            price={expense.amount} 
            date={expense.date} />
       )
      }
      </Card>
    </div>
    )
}
export default Items;
